INSERT INTO `baithuoc` (`maBaiThuoc`, `tenBaiThuoc`, `moTaTacDung`, `maBenh`, `cachDung`, `trangThai`) VALUES
(1, 'Hoạt mạch kim đan', 'Trúng phong không nói, bán thân bất toại, chân tay tê dại, đờm dãi trào lên, họng đau khó nuốt, ăn uống không được, hàm răng cắn chặt và giải tất cả mọi thứ dược độc, tửu độc, phát nhiệt bụng trướng đại tiểu tiện không lợi, hung cách bí đầy, trên thực dưới hư, khí bế mặt đỏ, làm ra mồ hôi rồi dư nhiệt không hết, lao bệnh uống nhiều thứ thuốc không khỏi, tai gái, già trẻ, phụ nữ đều uống được.', 22, 'Các vị thuốc trên trộn đều tán nhỏ, luyện mật với nước làm viên làm tễ, cứ 30g làm 10 viên, lấy Chu sa làm áo ở nơi ẩm thấp, lấy vàng bạc thật 40 lá làm áo cho vào lọ nút kín lâu năm không hỏng, như chữa thống phong độc, uống với nước chè, giải thuốc độc, chiêu bằng nước lạnh, sau khi làm ra mồ hôi còn dư nhiệt, lao bệnh và trẻ con kinh nhiệt, nước Bạc hà làm thang.', 1),
(2, 'Độc hoạt tục mạnh thang', 'Bỗng nhiên trúng phong, nửa người bại liệt, miệng mắt méo xệch, chân tay co giật, nói năng khó khăn, tê dại khắp người tinh thần hỗn loạn, nhức đầu chóng mặt, đườm rãi rất nhiều, gân mạch co lại, khớp xương buồn đau, khó quay trở. Còn trị mọi chứng phong, uống vào đều khỏi. Nếu chữa chwunsg cước mềm yếu chữa lâu cũng khỏi. Người bị cảm gió lâu ngày, mỗi khi gặp thời tiết thay đổi nên uống trước để dự phòng chướng ác.', 12, 'Mỗi lần lấy 9g chi vào 1 chén rưỡi nước, cho 5 miếng gừng, đun còn 1 chén uống trước bữa ăn.', -1),
(3, 'Xà yết tục mạnh thang', 'Chợt trúng cấp phong, hàm răng cắn chặt tinh thân hôn mê, miệng mắt méo lệch, bất tỉnh nhân sự đờm rãi đưa lên cổ có tiếng rít.', 4, 'Mỗi lần lấy 15g cho vào 1 chén nước, cho thêm 3 miếng gừng, đun can còn 7 phân bỏ bã, uống hơi nóng, không kể giờ giấc.', -1),
(4, 'Long xạ tử chi tiễn', 'Tất cả mọi chứng phong, bán thân bất toại, miệng méo, mắt lệch, chóng mặt, ù tai, mũi ngạt cổ khô, chân tay tê dại, đau nhức, đờm độc đọng lại, lưng gối đau mỏi, gân co xương lạnh, mồm ngứa ngoài da, hôn mê mỏi mệt, ăn uống lúc được lúc không, đi lại mệt nhọc.', 30, 'Tán thật nhỏ. sao mễ phấn 120g màu vàng, cho gạo nếp nấu cháo, gia Bạch mật 60g hòa đều, làm thành thỏi dài 1,5 tấc, mỗi lần uống 1 thỏi nhai nát chiêu bằng nước chè hoặc rượu, bệnh nặng mỗi ngày uống 3 lần, mỗi lần uống 3 thỏi.', -1);
