(00670, 'Lêkima - Trứng gà', '', '', '', 670),
(00671, 'Sầu riêng', '', '', '', 671),
(00672, 'Dưa hấu', 'Thanh thử, giải nhiệt, lợi tiểu, huyết áp cao, nóng trong bàng quang, đái buốt, viêm thận, phù thũng, vàng da, đái dường, say rượu, cảm sốt, phiền khát.', '', 'Được trồng ở hầu hết các tỉnh miền BẮc va miền Nam,Có vị ngọt, tính hơi hàn.', 672),
(00673, 'Đậu nành', 'Làm thức ăn cho trẻ sơ sinh, người bị bệnh đường tiện, người bị thấp khớp, gút, ngươi mới ốm dậy, người lao động quá sức.', '', '', 673),
(00674, 'Đậu xanh', 'Tiêu tích nhiệt, giải bách độc, tiêu thũng phù, hạ khí, giải nhiệt độc, giải các chất độc của thuốc và kim loại.', '', 'Hạt đậu xanh có vị ngọt, tính nhiệt không độc.', 674),
(00675, 'Nhục thung dung', 'Tư âm, bổ thận, ích tinh, huyết tráng dương hoạt trường. Dùng trong trường hợp liệt dương, lưng gối lạnh đau,vo sinh bạch đới khí hư, huyết khô, táo bón.', '', 'Vị ngọt, chua, tính hơi ôn, khong có độc. Có tài liệu nói cay, ôn , đại nhiệt.', 675),
(00676, 'Tích dương', 'Bổ thận, hoạt trương mạnh lưng gối, nam liệt dương, nữ vô sinh, huyết khô, đại tiện táo bón, lưng gối yếu mỏi.', '', 'Vị ngọt, tính ôn.', 676),
(00677, 'Tầm sét', 'dùng rễ củ tầm sét làm thuốc bổ, tăng dục, điều kinh tránh béo bệu. ngoài ra còn chưa suy yếu rong kinh.', '', '', 677),
(00678, 'Cây xa kê', '', '', '', 678),
(00679, 'Lộc nhung, mê nhung', 'làm tăng sức mạnh của cơ thể giảm sự mệt mỏi của cơ tim, làm những vết thương bên ngoài, nhất la các mụn nhọt có mủ mau lành', '', '', 679),
(00680, 'Lộc giác', 'Là vị thuốc bổ dùng trong các trường hợp mệt mỏi, thần kinh suy nhược, làm cho máu chạy điều hoàm chữa khớp xương bị sưng phù điểu trị các bênh ho, ho lao tiểu tiện ra huyết đi tiểu ra tinh dich mụn nhọt.', '', '', 680),
(00681, 'Cao ban long', 'là một vị thuốc bồi dưỡng, thuốc bổ, thuốc cầm máu dùng trong các trường hợp thổ huyết ho ra máu, dạ dày và ruột chảy máu. tử cung ra máu, kinh nguyệt quá nhiều, đi tiểu nhiều, mồ hôi trộn chân tay đai nhức', '', '', 681),
(00682, 'A giao - Minh giao', 'bắng huyết, lỵ ra máu, ho ra máu,đại tiểu tiện ra máu,hồi hộp mất ngủ', 'người tỳ vị hư nhược, nôn mửa, ỉa lỏng, tiêu hóa kem không dùng được', 'vị ngọt tính bình, vào kinh phế, can và thận', 682),
(00683, 'Nhau sản phụ', 'chữa gầy yếu, ho xuyễn,đau nhức trong xương,di tinh, hoạt tinh', 'người có thực tà không dùng được', 'vị ngọt, tính ôn, mặn, vào 2 kinh can và thận', 683),
(00684, 'Hải mã', 'kích thích tình dục lâu dài, đau bụng, khó đẻ', '', 'tính ôn, vị ngọt, không độc', 684),
(00685, 'Huyết lình', 'bổ máu, trẻ chậm lớn, kém ăn', '', '', 685),
(00686, 'Tắc kè', 'chữa hen,lao phổi và cường dương', 'Người có ẩm hen xuyễn', '', 686),
(00687, 'Mật ong', 'thanh nhiệt bổ trung,nhuận táo, hoạt trường,giải độc.dùng chữa tỳ vị nhược, táo bón, ho, đau bụng, chữa lở', '', 'vị ngọt, tính bình, vào 5 kinh,tâm phế,tỳ,vị và đại tràng', 687),
(00688, 'Nọc ong', 'sưng khớp do thấp,thấp cơ, suyễn, viêm khí quản,nhức đầu, huyết áp cao', 'người bệnh lao,bệnh gan và tuyến tụy tạng,bệnh thận kèm đái ra máu, phụ nữ có thai và cho con bú', 'chất lỏng rất sánh,không màu,mùi đặc biệt na ná như mùi mật ong, vị bỏng đắng', 688),
(00689, 'Ong đen', 'thanh nhiệt, sâu răng, lở loét miệng, đau cổ họng', 'người hư hàn', 'vị ngọt chua, tính hàn, không độc', 689),
(00690, 'Yến', 'hư yếu, ho lao, sốt từng cơn, hen xuyễn, thổ huyết', 'người hư hàn', 'vị ngọt, tính bình, vào hai kinh phế và vị', 690),
(00691, 'Bạch cương tàm', 'chữa kinh giản,cổ họng sưng đau, trúng phong mất tiếng, đầu phong răng đau', 'người hư huyết không phải phong tà không dùng được', 'vị mặn, cay, tính bình va không có độc, vào 4 kinh tâm , can tỳ và phế', 691),
(00692, 'Bọ cạp', 'trấn kinh, chưa trẻ em kinh phong, uốn ván; còn làm thuốc kích thích thần kinh, bán thân bất toại', 'người hư huyết sinh phong không dùng được', 'vị mặn, hơi cay, tính bình, có độc, vào kinh can', 692),
(00693, 'Nhựa cóc', 'chữa bệnh chó dại, còi xương, chậm lớn, mụn nhọt, đau răng', '', 'vị ngọt, cay,tính ôn, có độc, vào kinh vị', 693),
(00694, 'Dạ minh sa', 'bệnh về mắt, quáng gà,', 'người hư hàn', 'vị cay, hàn, không có độc, vào can kinh', 694),
(00695, 'Dế', 'lợi tiểu tiện, chữa thủy thúng, thông đại tiện, chữa khó đẻ', '', 'vị mặn, tính hàn,có độc, vị cay, mặn ', 695),
(00696, 'Nước bọt', 'chữa mụn nhọt sưng đau,ghẻ lở, phỏng da,làm sáng mắt', '', 'vị mặn, tính bình, không độc', 696),
(00697, 'Nước tiểu', 'chữa hàn nhiệt, đau đầu,cầm máu, bổ âm, giáng hỏa', '', 'vị mặn, tính hàn, không độc', 697),
(00698, 'Phân người', 'chữa ngộ độc khoan sắn,nấm độc, nem, thịt', '', '', 698),
(00699, 'Giun đất', 'thanh nhiệt trấn kinh, lợi tiểu giải độc, ho xuyễn, kinh phong, toàn thân bất toại, tiểu tiện khó khăn', 'không phải thực nhiệt không dung được', 'vị mặn, tính hàn,vào 3 kinh tỳ, vị và thận', 699),
(00700, 'Xương hổ', 'Đau xương, tê thấp, đi lại khó khăn. Còn được sử dụng trong các trường hợp cảm gió, điên cuồng và được dùng lalfm thuốc bổ', '', '', 700),
(00701, 'Khỉ', 'Bổ máu, bổ toàn thân cho phụ nữ trong trường hợp kém ăn, kém ngủ, thiếu máu xanh xao và hay đổ mồ hôi trốm', '', '', 701),
(00702, 'Long duyên hương', 'Lợi khí, hoạt huyết làm giảm đau, sát trùng, dùng trong ho hen suyễn đau trong tim, trong bụng', '', 'Là những cục trong mờ, không đều có cục nặng 5-6kg nhưng có cục nặng gần tạ. Có màu sắc đa dạng, nếm hơi ngọt, hơi chua, dung nóng chảy mềm như sáp, tan quá nửa trong cồn, khi đốt có mùi thơm dễ chịu', 702),
(00703, 'Hổ phách', 'An thần, định kinh, lợi tiểu, tán ứ huyết, Dùng trong những trường hợp hay mất ngủ, tiểu tiện ra huyết, chữa mụn nhỏ', 'Chỉ dùng cho những người thủy thịnh hỏa suy', 'Là những cục to nhỏ không đều màu vàng hoặc vàng đỏ, không có vụ gì tỏa mùi hương dễ chịu khi đốt không tan trong nước nhưng tan trong cồn, ete, colophic', 703),
(00704, 'Miết giáp', 'Bổ ấm, lao gầy, nhức xương, lao lực quá độ, ho lao, tiểu tiện ra sạn, kinh nguyệt bế, bổ dưỡng nói chung', 'Không dùng cho phụ nữ có thai', '', 704),
(00705, 'Nhân trung bạch', 'Thanh nhiệt, giáng hỏa, khử ứ, cầm máu. Được dùng làm thuốc tả hỏa, thanh nhiệt dùng trong những bệnh cổ họng sưng đau, chảy máu cam, thiên đầu thống, cam tẩu mã, lở mồm lưỡi, do sốt lâu ngày mà gầy còm, còn dùng làm thuốc ho, thuốc bổ', '', '', 705),
(00706, 'Nhện', 'Thường được dùng chữa chảy máu không ngưng, mụn nhọt, viêm cổ họng , đái dầm, mồ hôi trộm', '', '', 706),
(00707, 'Ngũ linh chi', 'Thông lợi huyết mạch, hành ứ hết đâu, thấy kinh đau bụng, đẻ xong huyết xấu ra không hết sinh đau bụng, ngự đau, trẻ con bị cam, dùng ngoài chữa rắn rết cắn', '', '', 707),
(00708, 'Rắn', 'Thịt rắn được đông y coi là một vị thuốc có công dụng chữa bệnh thần kinh đau nhức, tê liệt, bán thân bất toại, các cơn co giật, chữa nhọt độc, bị cảm trợn mắt miệng méo. Mật rắn thường dùng để chữa ho đau lưng, nhức đầu khó chữa. Xác rắn có tác dụng khứ phong sát trùng, tan mộng, dùng chữa chứng kinh nguy hiểm của trẻ em, sát trùng trị đau cổ họng.', 'Mật rắn có độc nên dùng với liều thấp', '', 708),
(00709, 'Xạ hương', 'Được dùng một cách phổ biến làm thuốc trấn kinh, chữa bệnh suy nhược thần kinh, trúng phong, mê man, choáng váng, đau mắt cam tấu mã....', '', '', 709),
(00710, 'Quy bản và cao quy bản', 'Bổ thận âm, chữa ho lâu, di tinh, bạch đới, khí hư, chân tay lưng gối đau nhức. Còn dùng đẻ chữa lỵ kinh niên, sốt rét lâu ngày cơ thể suy nhược, băng huyết, các bệnh trước và sau khi  sinh nở, trẻ con uyết xương', '', '', 710),
(00711, 'Tàm sa', 'Trừ phong thấp, hóa huyết ứ, chữa đau mắt đỏ, chân tay tê dại', '', 'Có vị ngọt, cay tính ôn, không độc', 711),
(00712, 'Tê giác', 'Làm mát huyết, giải ôn đọc và định kinh, thường dùng trong các trường hợp sốt quá hóa điên cường , sốt vàng da, thổ huyết, máu cam, nnhuwsc đầu, ung độc, hậu bối', '', '', 712),
(00713, 'Thạch sùng', 'Chữa mụn nhọt, thần kinh suy nhược, bệnh về dạ dày và ruột, tiêu hóa kém, kém ăn, bán thân bất toại, viêm khớp mãn tính, đau thần kinh, nhức đầu kinh niên mà không rõ nguyên nhân', '', '', 713),
(00714, 'Tóc rối', 'tiêu ứ, cầm máu, dùng àm thuốc trấn kinh trẻ con, chưa các chứng lỵ, sang lở, đậu mùa. Nhân dân dùng huyết dư thán để chữa thổ huyết, máu cam, lỵ ra máu, tiểu tiện ra máu, đại tiểu tiện khó khăn. Khi nấu cao dán nhọt thì có tác dụng cầm máu, lên da', 'phải dùng đốt không dùng sống. nếu dùng sống thì chỉ dùng nấu cao dán mụn nhọt', 'có vị đắng, tính hơi ấm, không độc, vào 3 tâm kinh, can và thận', 714),
(00715, 'Da voi', 'chữa những bệnh mụn nhọt lở loét lâu ngày không liền miệng', '', 'vị ngọt, mặn tính ấm(ôn)', 715),
(00716, 'Xuyên sơn giáp', 'tán huyết thông lạc, tan ung nhọt, làm thuốc chữa đậu, trẩn, tắc tia sữa.', 'ung thư đã vỡ rồi mà nguyên khí hư thì không dùng được', 'vị mặn tính hơi hàn, có độc, vào 2 kinh can và vị', 716),
(00717, 'Bìm bịp', 'chữa đau lưng, suy nhược của tuổi già', '', '', 717),
(00718, 'Cà cuống', 'dùng cới liêu nhỏ thì có tác dụng kích thích thần kinh và hưng phấn bộ phận sinh dục', 'dùng với liêu cao có thể gây ngộ độc', 'tinh dầu tan trong mỡ, độ sôi 168-170', 718),
(00719, 'Cá nóc', 'với liều lượng rất nhỏ được dùng làm chất làm dịu ở giai đoạn cuối của ung thư', 'độc tính rất cao', '', 719),
(00720, 'Cá trắm', 'trị tắc họng, mắt mờ, chữa mắt đỏ kéo màng, chữa họng mọc mụn, sưng tê, chữa cửa mình sưng cứng như đá, đau đớn, trẻ con đờm dãi ủng trệ', 'cẩn thận liêu lượng để tránh ngộ độc', '', 720),
(00721, 'Chim sẻ', '', '', '', 721),
(00722, 'Chó', 'ôn bổ tì thận, trừ hàn, trợ dương là vị thuốc cường tráng dùng yên ngũ tạng, nhẹ người, ích khí', '', 'vị mặn, chua, tính nóng, không độc', 722),
(00723, 'Dê', 'tiết dê làm bổ huyết, chữa choáng váng, chóng mặt nhức đầu, đau lưng.Thịt dê trợ dương, bổ tinh huyết, dùng chữa ho lao, phụ nữ  khi đẻ gầy yếu, khí hư cạn sữa, huyết hôi. Gan dê chữa phong hư nhiệt, mắt mờ đỏ. Tinh hoàn dê trị thận yếu, tinh hoạt. Dạ dày dể chữa  gây yếu, ă vào bị nôn mửa', '', 'Thịt dê có vị ngọt tính rất nóng', 723),
(00724, 'Đồi mồi', 'thanh nhiệt giải độc, dùng chữa bệnh nhiệt, mê sảng, kinh giản, ung nhọt, sưng tấy, nốt đậu hãm đen', '', 'vị ngọt tính bàn, vào hai kinh tâm và can', 724),
(00725, 'Kỳ đà', 'chữa hen suyễn, động kinh', '', '', 725),
(00726, 'Nhím', 'có tác dụng lương huyết, giải độc, làm hết đau, trĩ lậu ra huyết. Chữa những trường hợp trĩ lòi dom chảy máu, di mộng tinh, nôn mửa, lỵ ra máu', '', 'có vuh đắng, ngọt, tính bình', 726),
(00727, 'Thằn lằn', 'chữa hen xuyến, gầy yếu ăn', '', '', 727),
(00728, 'Trăn', 'Cao trăn chữa đau nhức xương, đặc biệt là đau cột sống. Máu trăn chữa hoa mắt, choáng váng, mỏi lưng. ', '', 'Sống hoang dại ở các vùng núi nước ta. Trăn không có răng độc như rắn, bắt mồi bằng cách ngoạm lấy chân kẻ thù, lấy thân quấn cho con mồi chết ngạt rồ nuốt. Trăn cho thịt ăn ngon, da làm đồ dùng, xương và máu dùng để làm thuốc.', 728),
(00729, 'Rươi', 'Là thức ăn bổ dưỡng vì chứa nhiều chất đạm. Ngoài ra còn làm thức ăn cho cá.', 'Những người bị hen tránh ăn rươi.', 'Ở miền Bắc vào những ngày cuối tháng 9, đầu tháng 10 âm lịch, khi trời đang bình thường bỗng mát hẳn, nhiệt độ dưới 25 độ, vòm trời u ám, có mưa lất phất ... đó là dấu hiệu rươi xuất hiện từng đàn trên mặt nước.', 729),
(00730, 'Con sam', 'Máu sam được chế ra dùng để chuẩn đoán các bệnh nhiễm khuẩn do vi trùng gram gây ra. Vỏ sam chữa bệnh gà lên đậu, sởi, chữa rong huyết khi có thai.', '', 'Sống ở độ sâu 4-10m, nhiệt độ là 20-32 độ, độ mặn khoảng 18-33%. Bắt đầu từ tháng 4  sam bơi vào bờ để đẻ trứng.', 730),
(00731, 'Đỉa', 'Trước đây đỉa dùng để hút mấu trog nhiều trường hợp viêm tấy. Làm nguyên liêu chiết men hirudin để chữa những trương hợp máu đông tắc, viêm màng bao tim, trĩ, tụ máu nội tang, tụ máu ở các vết thương.', 'Khi dùng đỉa để hút máu thì khong dùng cho người già, trẻ e, và những người máu khó đông. Việc dùng đỉa hút máu rất nguy hiểm vì có thể bị mắc bệnh truyền nhiễm.', 'Sống dưới nước lặng hay nước chảy chậm. Hút dich cỏ, chất dinh dưỡng của động thực vật thủy sinh và thích hút máu động vật và con người. Một con đỉa hút khoảng 15g máu trước khi nó nhả ra, và khi đã nhả ra thì khoảng 15g máu nữa chảy ra do không cầm được máu.', 731),
(00732, 'Hải sâm', 'Làm thực phẩm cao cấp bồi dưỡng. Chữa viêm phế quản, thần kinh suy nhược, cầm máu. Trong y học cổ truyền là vị thuốc bổ thận, tráng dương, bổ âm, ích tinh, thông chướng, nhuận táo, chữa ly.', '', 'Phân bó nhiều ở nước ta.Ngư dân đánh bắt về đem phơi hoặc sấy khô. Loại tốt nhất là cso mầu đen thịt quánh dính, da có nhiều gai. Loại to mà mềm, da không có gai là loại kém.', 732),
(00733, 'Con quy', 'Chữa bệnh cam tích(ăn uống không tiêu, gầy yếu....) của trẻ em.', '', 'Quy thương sống ở nơi tích trữ lương thực, nhất là ngũ cốc.', 733),
(00734, 'Ốc sên', 'Dùng ốc sên nấu ăn chữa hen suyễn, đau bụng kinh niên, thấp khớp.', '', 'Là loài hoang dại, ăn hại hoa màu rau cỏ, đặc biệt vào mùa mưa phát triển rất nhanh.', 734),
(00735, 'Sâu ban miêu', 'Được dung chủ yếu bên ngoài  làm thuốc rộp da để gay mụn dần độc hay thuốc tụ bệnh.', 'Thuộc loại độc bảng A', 'Sống ở hầu hết các tỉnh ở nước ta. Khi đã băt được thì sấy khô, đựng vào lọ kín, để nơi kho ráo. Muốn bảo quản thì phải cho 1 ít long não hay thủy ngân vào đáy lọ. Hay sau khi lấy ra ở lò sấy, còn đang nóng thì cho luôn vào lọ tiệt trùng. Khi dùng còn sao với gạo nếp 1-2 lần để giảm bớt độ độc.', 735),
(00736, 'Diêm sinh', 'Bổ hỏa, tráng dương, bổ mệnh môn chân hỏa, lưa lợi đại trường sát trùng. Dùng trong trường hợp liệt dương, lỵ lâu ngày, người già yếu, hư hàn mà bí đại tiện, phong thấp. Dùng trong có tác dụng trừ giun sán. Dùng ngoài có tác dụng sát trùng, chứa mẩn ngứa, mụn nhọt.', '', 'Là một nguyên tó cso sẵn trong thiên nhiên, hoặc điều chế từ những hợp chất cso lưu hoàng trong thiên nhiên.Là bột màu vàng, không mùi, cso khi là cục to không đều, màu vang tươi có mùi đặc biệt, không tan trong nước, rượu và ete, tan nhiều trong dầu. Khi đốt cháy có ánh lửa xanh, khét, khó thở.', 736),
(00737, 'Duyên đơn', 'Uống trong có tác dụng long đờm, chấn tâm. Bôi ngoài có đuổi được độc và làm cho da mau lên da non. Thương dùng chữa nôn ọe, ăn vào nôn ra, trừ nhiệt đốc, cầm máu, thổ huyết, khái huyết', 'Những người hư hàn, không thực nhiệt không dùng được.', 'Là một vị thuốc cso vị cay, tính hơi lạnh và không độc.', 737),
(00738, 'Đảm phàn', 'Dùng chữa bệnh ở cổ họng, làm thuốc gây nôn, chữa đau mắt, sâu răng, mũi mọc thịt, rắn cắn, cam mũi, hôi nách. Làm thuốc sát trùng, thuốc diệt sâu bọ.', '', 'Có vị chua, cay, tính hàn, hơi độc, vào kinh can.', 738),
(00739, 'Hàn the', 'Chữa sốt, tiêu viêm, giải độc, viêm cổ họng, viêm hạnh nhân, răng lợi sinh loét, miệng và mắt đau.', '', 'Là tinh thể màu trắng, có vị nồng và không có mùi.Có vị ngọt, mặn, tính mát. ', 739),
(00740, 'Hoạt thạch', 'Làm thuốc bôi như phấn rôm. Chữa sốt, tả, lỵ, lợi tiểu tiện, sốt khát nước, viêm ruột, da vàng tiểu tiện ra máu,viêm niệu đạo hay có sỏi ở bàng quang.', 'Người âm hư không thấp nhiệt, tỳ hư hạ hãm, phụ nữ có thai.', 'Là chất bột trắng mịn, sờ trơn mát, không tan trong nước. Có vị ngọt tính hàn, không có độc, vào 2 kinh vị và bàng quang.', 740),
(00741, 'Hùng hoàng và Thư hoàng', 'Có năng lực thẩm tháp, sát trùng, giải độc, chữa ghẻ, đau mắt, thịt mọc trong mũi, trừ nọc rắn, tràng nhạc, nọc giang mai, sốt rét, trừ đờm', '', 'có vị đắng hơi cay, tính ôn có độc, vào 2 kinh can và vị', 741),
(00742, 'Khinh phấn', 'trừ giun, làm đi ngoài, lợi tiểu, thông mật, có khả năng trừ được tích trệ trong ruột và dạ dày, có thể chữa được các chứng thủy thũng phong đàm, thấp nhiệt', 'dùng nhiều có độc, dùng nhiều quá gân co, xương nhức, răng lung lay, khi không có thuốc khác mới nên dùng', 'vị cay, tính lạnh, không có độc', 742),
(00743, 'Long cốt', 'có khả năng trấn kinh, an thần, sáp tinh và làm hết mồ hôi, chữa hồi hộp mất ngủ, thần trí không yên, mồ hôi trộm, xích bạch đới, tả lỵ lâu ngày, vết loét lâu ngày không lành', '', 'vị ngọt, sáp, tính bình', 743),
(00744, 'Lô cam thạch', 'có tác dụng cầm máu, tiêu thúng độc, làm cho lên da non, sáng mắt, tan màng, thường chỉ dùng ngoài, liều lượng tùy theo vết loét', '', 'có vị ngọt, tính ôn, không độc', 744),
(00745, 'Lục phàn', 'gây nôn và cầm máu, chữa đau dạ dày, ruột chảy máu, cổ họng sưng đau, loét miệng', 'tì vị hư nhược và không tích trệ không nên dùng', 'chua mát và không độc', 745),
(00746, 'Mật đà tăng', 'vào kinh can có tác dụng trừ đờm, sát trùng, thu liễm trấn kinh; chữa ngũ trĩ, tẩy vết xạm ngoài da, chủ yếu chế cao dán nhọt', 'người trúng hàn không phải thực tà cấm dùng', 'có vị mặn, cay, tính bình và hơi độc', 746),
(00747, 'Muối ăn', 'có tác dụng tả hỏa, thanh tâm, lương huyết, nhuận táo, dẫn các thuốc khác vào kinh lạc; dùng trong trường hợp nhiệt kết ruột và dạ dày, táo bón, đau răng, mắt đỏ đau, gây nôn mửa, chữa hạ bộ lở ngứa', '', 'có vị mặn, tính hàn không độc', 747),
(00748, 'Phèn chua', 'có tác dụng táo thấp, giải độc, sát trùng, làm hết ngứa; dùng làm thuốc thu liễm, cầm máu, chủ yếu dùng chữa có nhiệt trong xương tủy, thịt mọc trong mũi, chế luyện thành thuốc chữa đau răng, đau mắt, lỵ, còn dùng làm thuốc cầm máu, chữa hoa ra máu, các loại xuất huyết', '', 'có vị chua, lạnh (hàn), không độc', 748),
(00749, 'Phục long can', 'có tác dụng ôn trung, cầm nôn, cầm máu; thường được dùng làm thuốc chữa bệnh băng huyết, thổ huyết, tiểu tiện ra máu, làm ấm ở trong chữa nôn đặc biệt thích hợp với phụ nữ có thai, trẻ con đái dầm; nếu bị ung nhọt thì hòa phục long can với dấm đắp vào', '', 'vị cay, tính hơi nóng (ôn) không có độc', 749),
(00750, 'Thạch cao', 'có tác dụng thanh nhiệt giáng hỏa, trừ phiền, chỉ khát; dùng trong các bệnh nhiệt, tráng nhiệt, mồ hôi trộm, phiền khát, miệng khô, lưỡi khô sốt quá phát cuồng, phổ nhiệt sinh ho, vị hỏa sinh nhức đầu, đau răng', 'Thạch cao là một vị thuốc đại hàn, nếu gặp lửa nguy hiểm chết người. Người vị nhược, không thực nhiệt không dùng được', 'có vị ngọt, cay, tính hàn', 750),
(00751, 'Thạch tín', 'có tác dụng trừ đờm, chữa sốt rét, ăn hết những chỗ thịt rữa thối nát; còn có tác dụng bổ máu, chữa thiếu máu, vàng da', 'tránh dùng nhiều quá gây ngộ độc', 'có vị vchua, cay, tính nóng, rất độc', 751),
(00752, 'Thăng dược', 'dùng chữa vêt sloét lâu lành, mụn nhọt, ung thư sang độc', 'thuốc có độc phải dùng cẩn thận', '', 752),
(00753, 'Thuỷ ngân', 'chữa mụn nhọt, giang mai, trừ ghẻ lở, nhiệt độc, làm trụy thai, có khi dùng chữa cháy trên đầu tóc; thường chỉ dùng ngoài', 'thuốc có độc phải dùng cẩn thận', 'có vị cay, tính hàn, lạnh và có độc', 753),
(00754, 'Lỗ địa cúc', '', NULL, NULL, 754),
